import logo from './logo.svg';
import './App.css';

export default class TresureHuntApp extends Component { 
constructor(props) {
        super(props);
		this.findTresure = this.findTresure.bind(this);
		this.inputArray = this.inputArray.bind(this);
        this.state = {"inputArray": null,
			status:"", cellsVisisted:[],statusMessage:""};
      }


   async componentDidMount() {
    const response = await fetch('/tresure-hunt');
    const body = await response.json();
    this.setState({tresure: body});
  }

  async findTresure(inputArray) {
	alert(inputArray);
    await fetch(`/tresure-hunt`, {
	  body: document.getElementById("inputArray").innerHTML,	
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    }).then(() => {

    });
  }

render() {
	const {tresure} = this.state;
    return (
	<>
		<div class="App">
		<header className="App-header">
		<img src={logo} className="App-logo" alt="logo" />
          <div className="App-intro">
			<h1>Please enter a valid array, elements of array should be separated with comma(,) </h1>
			 <label for ="inputArray">Input Array:</label>
			 <input type="text" id="inputArray" name="inputArray"/>
			 <input type="submit" value="Submit" onClick={this.findTresure.bind(this.state.inputArray)}/>
		</div>
		</header>
		</div>


    </>






    );
  }
}
